// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.8.0;

contract Quiz {
    address owner;
    string desc;
    string encodedQuiz;
    uint8[] answers;
    mapping(address => uint8[]) studentAnswers;

    constructor(
        string memory _description,
        string memory _encodedQuiz,
        uint8[] memory _answers,
        address _owner
    ) public {
        owner = _owner;
        desc = _description;
        encodedQuiz = _encodedQuiz;
        answers = _answers;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, 'You are not the owner of this quiz');
        _;
    }

    function getInfo() public view returns (
        string memory description,
        string memory questions
    ) {
        description = desc;
        questions = encodedQuiz;
    }

    function getAnswers() public view onlyOwner returns (uint8[] memory) {
        return answers;
    }

    function answerQuiz(uint8[] memory _answers) public {
        require(studentAnswers[msg.sender].length == 0, 'You already answered this quiz, there are no chances left');
        studentAnswers[msg.sender] = _answers;
    }

    function getScore() public view returns (uint score) {
        score = 0;
        for (uint8 i; i < answers.length; i++) {
            if (answers[i] == studentAnswers[msg.sender][i]) {
                score += 1;
            }
        }
        score /= answers.length * 100;
    }
}
